import (
	"errors"
	"fmt"

	"github.com/aws/aws-sdk-go/aws"
// container dependency status constants.
const (
	dependsOnStart    = "start"
	dependsOnComplete = "complete"
	dependsOnSuccess  = "success"
)
	"github.com/aws/copilot-cli/internal/pkg/manifest"
