func txEncode(tx transactions.Transaction, ad transactions.ApplyData) (res v1.Transaction, err error) {
	if tx.Type == protocol.PaymentTx {
		return paymentTxEncode(tx, ad), nil
	}
	if tx.Type == protocol.KeyRegistrationTx {
